#include<bits/stdc++.h>

using namespace std;

string f = "";
string res;

int main(void) {
    for (int i = 0; i < f.size(); i++) {
        if (f[i] == 'Z') {
            res.push_back('0');
            i += 3;
        } else if (f[i] == 'O') {
            res.push_back('1');
            i += 2;
        }
    }
    cout << res.size() << '\n';
    cout << res;
}